SELECT *,(SELECT TOP 1 nombre FROM SimulacionKarelotitlan.dbo.Algoritmo WHERE Algoritmo.id = (SELECT TOP 1 idAlgoritmo FROM simulacionKarelotitlan.dbo.Recomendacion WHERE Recomendacion.idSimulacion = Simulacion.id)) AS algoritmo,(SELECT TOP 1 descripcion FROM SimulacionKarelotitlan.dbo.Algoritmo WHERE Algoritmo.id = (SELECT TOP 1 idAlgoritmo FROM simulacionKarelotitlan.dbo.Recomendacion WHERE Recomendacion.idSimulacion = Simulacion.id)) as algoritmoDescripcion FROM SimulacionKarelotitlan.dbo.Simulacion
